mapper.select:

SELECT n.id, n.title, n.content, n.create_at, n.update_at,
na.attachment_url
FROM notification n
LEFT JOIN notification_attachment na ON n.id = na.notification_id AND na.is_deleted = 0


		AND title LIKE CONCAT('%', #{searchCondition.title}, '%')


		AND n.create_at >= #{searchCondition.startAt}


		AND n.create_at <= #{searchCondition.endAt}

	AND n.is_deleted = 0

LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(id)
FROM notification n


		AND title LIKE CONCAT('%', #{title}, '%')


		AND n.create_at >= #{startAt}


		AND n.create_at <= #{endAt}

	AND n.is_deleted = 0
