mapper.select:
SELECT id, topic, description, school_year, semester, status, created_by, is_deleted, created_at, updated_at FROM thesis
AND topic LIKE CONCAT('%', #{searchCondition.topic}, '%')
AND school_year = #{searchCondition.year}
AND semester = #{searchCondition.semester}
AND status = #{searchCondition.status}
AND is_deleted = 0
LIMIT #{pageSize} OFFSET #{offset}

SELECT t.id, t.topic, t.description, t.school_year, t.semester, t.status, t.is_deleted, t.created_at, t.updated_at FROM thesis t LEFT JOIN thesis_outline_comment toc ON t.id = toc.thesis_id
AND toc.user_id = #{searchCondition.councilId}
AND toc.is_deleted = 0
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(id)
FROM thesis


		AND topic LIKE CONCAT('%', #{topic}, '%')


		AND school_year = #{year}


		AND semester = #{semester}


		AND status = #{status}

	AND is_deleted = 0

SELECT COUNT(t.id)
FROM
thesis t
LEFT JOIN
thesis_outline_comment toc ON
t.id = toc.thesis_id


		AND t.topic LIKE CONCAT('%', #{topic}, '%')


		AND t.school_year = #{year}


		AND t.semester = #{semester}


		AND t.status = #{status}


		AND toc.user_id = #{councilId}

	AND t.is_deleted = 0
